def palindromeIndex(s)
  if s == s.reverse
    return -1
  else
    temp = s.split("")
    i = 0
    j = temp.length-1
    while (i < j)
      temp.delete_at(i)
      if temp == temp.reverse()
        return i
      end
      temp = s.split("")
      temp.delete_at(j)
      if temp == temp.reverse()
        return j
      end
      temp = s.split("")
      i += 1
      j -= 1
    end
    return -1
  end
end

p palindromeIndex("hgygsvlfwcwnswtuhmyaljkqlqjjqlqkjlaymhutwsnwcflvsgygh")

